settings.theme_css:
{{ shop.metafields.searchtap.custom_css }}

body.searchtap:has(.shopify-section:first-child [allow-transparent-header]):not(:has(.shopify-section--header~.shopify-section-group-header-group)) .header {
    margin-block-end: 0;
}

body.searchtap:has(.shopify-section:first-child [allow-transparent-header]):not(:has(.shopify-section--header~.shopify-section-group-header-group)) .header:not(:hover,.is-solid,:has([open])) {
    --background: 243 243 243 / 1.0 !important;
    --border-color: 220 220 220 !important;
    --text-color: 92 92 92 !important;
    --header-show-transparent-logo: 0 !important;
    margin-block-end: 0;
}
.st-search-root .st-autocomplete-container #st-product-wrapper .st-product-container.st-carousel .st-row .st-column-33 {
    max-width: 20%;
    flex: 20%;
    flex-shrink: 0;
}
@media only screen and (min-width: 1025px) {
    .st-search-root .st-autocomplete-container.st-pre-search {
        min-width: calc(100% - 523px);
        max-width: calc(100% - 523px);
    --background: 243 243 243/1.0;
    background-color: rgb(var(--background))!important;
    }
}
